import React, { useRef, useState } from "react";

import "./App.css";
import UserList from "./UserList2";
import CreateUser from "./CreateUser";

function App2() {
  const [inputs, setInputs] = useState({
    username: "",
    email: "",
  });

  const { username, email } = inputs;

  const onChange = (e) => {
    const { name, value } = e.target;
    setInputs({
      ...inputs,
      [name]: value,
    });
  }; /* onChange라는 이벤트가 일어났을 때, 
  input의 name과 value를 타겟으로 잡고, input의 username과 email을 변경 */

  const [users, setUsers] = useState([
    {
      id: 1,
      username: "velopert",
      email: "public.velopert@gmail.com",
      active:true
    },
    {
      id: 2,
      username: "tester",
      email: "tester@example.com",
      active:false
    },
    {
      id: 3,
      username: "liz",
      email: "liz@example.com",
      active:false
    },
  ]);

  const nextId = useRef(4);

  const onCreate = () => {
    const user={
      id:nextId.current,
      username,
      email
    }
    setUsers([...users,user]);
    /* 또는 setUsers(users.concat(user)) */


    setInputs({ username: "", email: "" });
    nextId.current += 1;
  };

  const onRemove=id=>{
    setUsers(users.filter(user=>user.id!==id));
  } /* 작동 : userList에서 버튼 클릭 -> ()=>onRemove(user.id)를 통해
  onRemove 함수 호출 : 해당 user의 id를 가져옴 -> 각각의 user에 대해
  해댱 id가 아닌 user만 따로 뽑아서 users 배열을 새로 생성
  -> 해당 배열을 새롭게 users 스테이트로 변화시킴 */

  const onToggle=id=>{
    setUsers(users.map(user=>
      user.id===id?{...user, active:!user.active}:user))
  };

  return (
    <>
      {/* 상태관리를 위에서 하고, 그 값만 prop 인자로 넣기 */}
      <CreateUser
        username={username}
        email={email}
        onChange={onChange}
        onCreate={onCreate}
      />
      <UserList users={users} onRemove={onRemove} onToggle={onToggle} />
    </>
  );
}

export default App2;
