import React from "react";

import Hello from "./Hello";

import './App.css';

function App() {
  const name='react';

  const style={
    backgroundColor:'black',
    color:'aqua',
    fontSize:24,
    padding:'1rem'
  }

  return (
    <>
      <Hello/>
      <div style={style}>
      {name}
      </div>

      <div className="gray-box">{name}</div> 
      {/* 이 방식 더 많이 사용 */}
      {/* 아울러 주석도 이렇게 감싸줘야함 */}

      <Hello  //열리는 태그 안에선 주석 이렇게 사용 가능
      /> {/* 어쨌든 재료 안주면 props 없이 기본적으로 출력되고... */}

      <Hello name="react" color="red"/>

    </>
  );
}

export default App;
