import React, { useEffect } from "react";

function User({ user, onRemove, onToggle }) {
  useEffect(() => {
    console.log("user값 설정"); /* 첫번째 파라미터는 함수 */
    console.log(object);
    return () => {
      console.log("컴포넌트가 화면에서 사라짐");
    };
  }, [] /* 두번쨰 파라미터는 배열 : 비우면 컴포넌트가 처음 나타날 때만 하묘수 호출 */
    );

  return (
    <>
      <div>
        <b
          style={{
            cursor: "pointer",
            color: user.active ? "green" : "black",
          }}
          onClick={() => onToggle(user.id)}
        >
          {user.username}
        </b>
        <span>({user.email})</span>
        <button onClick={() => onRemove(user.id)}>삭제</button>
        {/* 등록과 달리 유저 하나하나마다 삭제버튼 부여해야 하므로 유저리스트에 추가한다. */}
      </div>
    </>
  );
}

function UserList({ users, onRemove, onToggle }) {
  /* 이 users는 App.js에서 users로 보낸,
App.js에서 usersTwo로 준 배열이 되고 */

  return (
    <>
      {users.map((user /* 배열 안 각각의 객체이며, */) => (
        <User
          user={user} /* 이건 위에서의 User user */
          key={user.id}
          onRemove={onRemove}
          onToggle={onToggle}
        />
      ))}
    </>
  );
}

export default UserList;
