import React
, { useState } from "react";

 function InputSample() {

    const [text, setText]=useState('');

    const onChange=(e)=>{
        setText(e.target.value);
    }

    const onReset=()=>{
        setText('');
    }



    return (
        <div>
            <input onChange={onChange} value={text}/>

            <button onClick={onReset}>초기화</button>
            <div>
                <b>값: {text}</b>
            {/* 이렇듯 state 및 지역변수(const로 정한) 는
            인자와 다르게 App에서 재료로 소환하지 않는다는 점에서 props와 다름  
            즉 내부에서 사용하려고 있는 것*/}
            </div>

        </div>
    )
 }

 function InputSample2(){

    const [input, setInput] =useState({
        name:'', nickname:''
    });

    const {name, nickname}=input;

    const onChange=(e)=>{
    const {value, name} = e.target; //추출
    setInput({...input, [name]:value})
    }

    const onReset=()=>{
        setInput({name:'', nickname:''})
    }

    return (
        <>
        <div>
            <input placeholder="이름" onChange={onChange} value={name}/>
            <input placeholder="닉네임" onChange={onChange} value={nickname}/>
            <button onClick={onReset}>초기화</button>
            <div>
                <b>값:</b> {name} ({nickname})
            </div>
        </div>
        </>
    );
 }

 export {InputSample, InputSample2};
