import React
, { useState } from "react";

 function InputSample() {

    const [text, setText]=useState('');

    const onChange=(e)=>{
        setText(e.target.value);
    }

    const onReset=()=>{
        setText('');
    }



    return (
        <div>
            <input onChange={onChange} value={text}/>

            <button onClick={onReset}>초기화</button>
            <div>
                <b>값: {text}</b>
            {/* 이렇듯 state 및 지역변수(const로 정한) 는
             */}
            </div>

        </div>
    )
 }

 export default InputSample;
