import React, { useRef, useState } from "react";

import "./App.css";
import UserList from "./UserList2";
import CreateUser from "./CreateUser";

function App2() {
  const [inputs, setInputs] = useState({
    username: "",
    email: "",
  });

  const { username, email } = inputs;

  const onChange = (e) => {
    const { name, value } = e.target;
    setInputs({
      ...inputs,
      [name]: value,
    });
  }; /* onChange라는 이벤트가 일어났을 때, 
  input의 name과 value를 변경 */

  const users = [
    {
      id: 1,
      username: "velopert",
      email: "public.velopert@gmail.com",
    },
    {
      id: 2,
      username: "tester",
      email: "tester@example.com",
    },
    {
      id: 3,
      username: "liz",
      email: "liz@example.com",
    },
  ];

  const nextId = useRef(4);

  const onCreate = () => {
    setInputs({ username: "", email: "" });
    nextId.current += 1;
  };

  return (
    <>
      {/* 상태관리를 위에서 하고, 그 값만 prop 인자로 넣기 */}
      <CreateUser
        username={username}
        email={email}
        onChange={onChange}
        onCreate={onCreate}
      />
      <UserList users={users} />
    </>
  );
}

export default App2;
