      active:false
    },
    {
      id: 3,
      username: "liz",
      email: "liz@example.com",
      active:false
    }
  ]
};

function reducer(state, action) {
  return state;
}

function App2() {
  const [state, dispatch] =useReducer(reducer, initialState);


  return (
    <>
      <CreateUser
      />
      <UserList users={[]} />
      활성사용자 수 : 0
    </>
  );
}

export default App2;
